package codes;

import java.util.Scanner;

public class codes {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n;
		Scanner sc=new Scanner(System.in);
		n=sc.nextInt();
		if (n<0)
		{
		    n=n*-1;
		    System.out.println(n);
		}
		else{
		      System.out.println(n);
		}
	}

}
