package operators;

public class operators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int num1=20;
int num2=30;
char operator='+';
if(operator == '+')
{
	System.out.println(num1+num2);

}
else if(operator == '-')
{
	System.out.println(num1-num2);
}
else if(operator == '*')
{
	System.out.println(num1*num2);
}
else if(operator == '/')
{
	System.out.println(num1/num2);
}

	}

}
